import React, { Component } from 'react'
import { Button, Input } from 'semantic-ui-react'
import NavBar from '../Nav';
import axios from 'axios'

class Search extends Component{
    constructor(){
        super();
        this.state = {
            currentUser: [],
            userInput:""
        }
    }
    componentDidMount(){
        this.getUser();
    }
    getUser = async () => {
        try{
            // console.log("get user function");
            const userId = localStorage.getItem('sessionUserId');
            // console.log(userId);
            const user = await fetch(`${process.env.REACT_APP_API_URL}/api/v1/users/${userId}`, {
                credentials: 'include',
                method: "GET"
            })

            const parsedUser = await user.json()
             console.log("currentUser:", parsedUser);
            this.setState({
                currentUser: parsedUser
            })

        } catch(err){
            console.log(err);
            this.props.history.push('/')
        }
    }
    searchButton = async () => {
        try {
            const searchPlant = await fetch(`&{process.env.REACT_APP_API_URL/api/v1/plants/search`, {
                credentials: 'include',
                method: 'get'
            })
            const parsedPlant = await searchPlant.json();
            console.log(parsedPlant);
        } catch(err){
            console.log("Things went wrong in your search button yo!")
        }
    }
    updateValue = (e) => {
            this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })
    }
    render(){
        return(
            <React.Fragment>
            <NavBar/>
                 <Input icon='leaf' onChange={this.updateValue} name="userInput" iconPosition='left' value={this.state.userInput} placeholder='Search plants...' />
                 <Button color="green" onClick={this.searchButton}>Search</Button>
            </React.Fragment>
        )
    }
}

export default Search;

