import React, { Component } from 'react'
import { Button, Input } from 'semantic-ui-react'
import axios from 'axios'

class Search extends Component{
    constructor(){
        super();
        this.state = {
            currentUser: [],
            userInput:""
        }
    }
    componentDidMount(){
        this.getUser();
    }
    getUser = async () => {
        try{
            // console.log("get user function");
            const userId = localStorage.getItem('sessionUserId');
            // console.log(userId);
            const user = await fetch(`${process.env.REACT_APP_API_URL}/api/v1/users/${userId}`, {
                credentials: 'include',
                method: "GET"
            })

            const parsedUser = await user.json()
             console.log("currentUser:", parsedUser);
            this.setState({
                currentUser: parsedUser
            })

        } catch(err){
            console.log(err);
            this.props.history.push('/')
        }
    }
    searchButton = async () => {
      const token = "eTdMWkZzcGdZenh4c2NadEFtR0pLZz09"
      const url = "https://trefle.io/api/plants/103505?token=" + token;

      axios.get(url)
      .then(function (response) {
        console.log(response);
      })
    }
    updateValue = (value) => {
      this.setState({userInput: value})
    }
    render(){
        return(
            <React.Fragment>


                 <Input icon='users' iconPosition='left' placeholder='Search users...' />

                  <input
                    type="text"
                    value={this.state.userInput}
                    onChange={this.updateValue}
                  />
                  <Button color="green" onClick={this.searchButton}>Search</Button>


            </React.Fragment>
        )
    }
}

export default Search;
