import React, {Component} from 'react';
import {Menu, Header, Button} from 'semantic-ui-react';
import {Link} from 'react-router-dom';

class NavBar extends Component{
    constructor(){
        super();
        this.state = {
            active: ""
        }
    }
    handleNav = async (e) => {
        await this.setState({
            active: e.currentTarget.value
        })
    }
    handleLogout = async (e) => {
        localStorage.setItem('sessionUserId', null);
    }
    render(){
        return(
            <Header>
                <Menu pointing secondary>
                    <Menu.Item color="rgba(51, 119, 39, 0.72)" name="home" active={this.state.active==="home"}>
                        <Link to="/home">
                            <Button value="home" onClick={this.handleNav} size='small'>Home</Button>
                        </Link>
                    </Menu.Item>
                    <Menu.Item name="search" value="search" active={this.state.active==="search"}>
                        <Link to="/search" value="search">
                            <Button size='small' value="profile" onClick={this.handleNav}>Profile</Button>
                        </Link>
                    </Menu.Item>
                    <Menu.Item position="right">
                        <Link to={{
                            pathname:'/',
                            state: {
                                loggedout: true,
                                msg: "You have successfully logged out"
                            }
                        }}>
                            <Button size='small' value="logout" onClick={this.handleLogout}>Logout</Button>
                        </Link>
                    </Menu.Item>
                </Menu>
            </Header>
        )
    }
}


export default NavBar;
